import { Nav, NavItem } from "reactstrap";
import { Link, useLocation } from "react-router-dom";
import './include.css';

const navigation = [
        {       
                tabId: "tab_1",
                title: "Main Ouslet",
                href: "/main",
        },
        {
                tabId: "tab_1",
                title: "Sell",
                href: "/sell",
        },
        {
                tabId: "tab_1",
                title: "Open/Close",
                href: "/badges",
        },
        {
                tabId: "tab_1",
                title: "Sales history",
                href: "/badges",
        },
        {
                tabId: "tab_1",
                title: "Cash Management",
                href: "/badges",
        },
        {
                tabId: "tab_1",
                title: "Status",
                href: "/badges",
        },
        {
                tabId: "tab_1",
                title: "Settings",
                href: "/badges",
        },
];

const SidebarList = (props) => {
        let location = useLocation();
        const printData = (navi, index) => {
                if(props.tabId == navi.tabId) {
                        return <NavItem key={index} className="sidenav-bg" style={{width:'145px'}}>
                                        <Link
                                                to={navi.href}
                                                className={
                                                        location.pathname === navi.href
                                                                ? "active nav-link py-3 sidebarlist"
                                                                : "nav-link text-secondary py-3 sidebarlist"
                                                }
                                        >
                                                <span className="d-inline-block ">{navi.title}</span>
                                        </Link>
                                </NavItem>
                }
                
        }
        
        return (
                <>
                { props.tabId && 
                        <div style={{background: 'white', paddingTop: '15px'}}>
                                <Nav vertical className="sidebarNav">
                                        { navigation.map((navi, index) => (
                                                printData(navi, index)
                                        ))}
                                </Nav>
                        </div>
                }
                </>

        );
};

export default SidebarList;
