import React, { useState } from 'react'
import { Link } from "react-router-dom";
import { Row, Col } from "reactstrap";
import PropTypes from 'prop-types';
import { useTheme } from '@mui/material/styles';
import { Box, Tabs, Tab, InputAdornment, InputLabel, Select, MenuItem, OutlinedInput, Input } from '@mui/material';
import AccountCircle from '@mui/icons-material/AccountCircle';
import "./sell.css"

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

const names = [
  'Oliver Hansen',
  'Van Henry',
  'April Tucker',
  'Ralph Hubbard',
  'Omar Alexander',
  'Carlos Abbott',
  'Miriam Wagner',
  'Bradley Wilkerson',
  'Virginia Andrews',
  'Kelly Snyder',
];

TabPanel.propTypes = {
     children: PropTypes.node,
     index: PropTypes.number.isRequired,
     value: PropTypes.number.isRequired,
};

function TabPanel(props) {
     const { children, value, index, ...other } = props;
   
     return (
          <div
               role="tabpanel"
               hidden={value !== index}
               id={`simple-tabpanel-${index}`}
               aria-labelledby={`simple-tab-${index}`}
               {...other}
           >
          {value === index && (
                <div>{children}</div>
          )}
       </div>
     );
}

function a11yProps(index) {
     return {
          id: `simple-tab-${index}`,
          'aria-controls': `simple-tabpanel-${index}`,
     };
}

function getStyles(name, personName, theme) {
  return {
    fontWeight:
      personName.indexOf(name) === -1
        ? theme.typography.fontWeightRegular
        : theme.typography.fontWeightMedium,
  };
}

const SaleHistory = () => {
     const theme = useTheme();
     const [value, setValue] = useState(0);
     const [personName, setPersonName] = useState([]);

     const handleChange = (event, newValue) => {
          setValue(newValue);
     };

     const selectChange = (event) => {
          const {
               target: { value },
          } = event;
          setPersonName(
               typeof value === 'string' ? value.split(',') : value,
          );
     };

     return(
          <div className='OpenClose'>
               <div>
                    <h2>Sales History</h2>
               </div>
                    
               <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                  <Tabs value={value} onChange={handleChange} TabIndicatorProps={{style: {background:'#3f32f5', color: 'green'}}}>
                    <Tab label="All" {...a11yProps(0)} />
                    <Tab label="Process return" {...a11yProps(1)} />
                    <Tab label="Continue sale" {...a11yProps(2)} />
                  </Tabs>
                </Box>
                <TabPanel value={value} index={0}>
                    <div className='OpenCloseHeader'>
                         <div><p>View, edit and manage your sales all in one place. <Link to=''>Need help?</Link></p></div>
                    </div>         
                    <div className='SalesHistory'>
                         <Row>
                              <Col xxl='3' md='3'>
                                   <InputLabel htmlFor="demo-multiple-name-label">
                                        Status
                                   </InputLabel>
                                   <Select
                                        labelId="demo-multiple-name-label"
                                        id="demo-multiple-name"
                                        multiple
                                        fullWidth
                                        value={personName}
                                        onChange={selectChange}
                                        input={<OutlinedInput label="Name" />}
                                        MenuProps={MenuProps}
                                   >
                                        {names.map((name) => (
                                        <MenuItem
                                        key={name}
                                        value={name}
                                        style={getStyles(name, personName, theme)}
                                        >
                                        {name}
                                        </MenuItem>
                                        ))}
                                   </Select>
                              </Col>
                              <Col xxl='3' md='3'>
                                   <InputLabel htmlFor="input-with-icon-adornment">
                                        Status
                                   </InputLabel>
                                   <Input
                                        id="input-with-icon-adornment"
                                        fullWidth
                                        startAdornment={
                                             <InputAdornment position="start">
                                             <AccountCircle />
                                             </InputAdornment>
                                        }
                                   />
                              </Col>
                              <Col xxl='3' md='3'></Col>
                              <Col xxl='3' md='3'></Col>
                         </Row>
                    </div>  
                </TabPanel>
                <TabPanel value={value} index={1}>
                  Item Two
                </TabPanel>

          </div>
     )
}
export default SaleHistory;
