import React from "react";
import { Nav, NavItem } from "reactstrap";
import { Link } from "react-router-dom";
import { AiTwotoneSetting } from "react-icons/ai";
import { BiHome, BiDollarCircle, BiCalendarAlt } from "react-icons/bi";
import { BsCart3, BsTagFill, BsPersonSquare } from "react-icons/bs";
import { MdOutlineFeaturedVideo, MdOutlineTimeline } from "react-icons/md";

const navigation = [
  {
    id: "tab_0",
    title: "Home",
    icon: <BiHome/>,
  },
  {
    id: "tab_1",
    title: "Sell",
    icon: <BiDollarCircle/>,
  },
  {
    id: "tab_2",
    title: "Calendar",
    icon: <BiCalendarAlt/>,
  },
  {
    id: "tab_3",
    title: "Ledger",
    icon: <MdOutlineFeaturedVideo/>,
  },
  {
    id: "tab_4",
    title: "Reporting",
    icon: <MdOutlineTimeline/>,
  },
  {
    id: "tab_5",
    title: "Sales",
    icon: <BsTagFill/>,
  },
  {
    id: "tab_6",
    title: "Customers",
    icon: <BsPersonSquare/>,
  },
  {
    id: "tab_7",
    title: "E-Commerce",
    icon: <BsCart3/>,
  },
  {
    id: "tab_8",
    title: "Settings",
    icon: <AiTwotoneSetting/>,
  },
];

const Sidebar = (props) => {
  const changeBackground = (id) => {
    props.getTabId(id)
    var elems = document.querySelectorAll(".nav-link.text-secondary.py-3.active");
    [].forEach.call(elems, function(el) {
        el.classList.remove("active");
    });
    document.getElementById(id).className += " active"
  }

  return (
    <div>
      <div>
        <Nav vertical className="sidebarNav">
          {navigation.map((navi, index) => (
            <NavItem key={index} className="sidenav-bg sideNavTab" onClick={() => changeBackground(navi.id)}>
              <Link id={navi.id} to='#' className="nav-link text-secondary py-3">
                <span>{navi.icon}</span>
                <span className="d-inline-block">{navi.title}</span>
              </Link>
            </NavItem>
          ))}
        </Nav>
      </div>

    </div>

  );
};

export default Sidebar;
