import { Nav, NavItem } from "reactstrap";
import { Link, useLocation } from "react-router-dom";
import './include.css';

const navigation = [
        {       
                tabId: "tab_1",
                title: "Home",
                href: "/starter",
        },
        {
                tabId: "tab_2",
                title: "Alert",
                href: "/alerts",
        },
        {
                tabId: "tab_3",
                title: "Badges",
                href: "/badges",
        },
];

const SidebarList = (props) => {
        let location = useLocation();
        console.log(props.tabId)
        return (
                <>
                { props.tabId && 
                        <div style={{background: 'white', paddingTop: '15px'}}>
                                <Nav vertical className="sidebarNav">
                                        { navigation.map((navi, index) => (
                                                <NavItem  className="sidenav-bg" style={{width:'145px'}}>
                                                        <Link
                                                                to={navi.href}
                                                                className={
                                                                        location.pathname === navi.href
                                                                                ? "active nav-link py-3 sidebarlist"
                                                                                : "nav-link text-secondary py-3 sidebarlist"
                                                                }
                                                        >
                                                                <span className="d-inline-block ">{navi.title}</span>
                                                        </Link>
                                                </NavItem>
                                        ))}
                                </Nav>
                        </div>
                }
                </>

        );
};

export default SidebarList;
