sourcesContent:
import React, { useState, useEffect } from 'react'
import axios from 'axios';
import { Link } from "react-router-dom";
import { Button } from "reactstrap";
import { FiArrowLeft, FiArrowRight } from "react-icons/fi";
import CreatableSelect from 'react-select/creatable';
import Select from 'react-select'
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import SunEditor from 'suneditor-react';
import Checkbox from '@mui/material/Checkbox';
import FormControlLabel from '@mui/material/FormControlLabel';
import Dropzone from 'react-dropzone-uploader'
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import { AiOutlinePlus, AiOutlinePercentage } from "react-icons/ai";
import { IoTrashOutline } from "react-icons/io5";
import { TbCurrencyDollar } from "react-icons/tb";
import { BsSearch } from "react-icons/bs";
import { ToastProvider, useToasts } from 'react-toast-notifications';
import 'react-dropzone-uploader/dist/styles.css'
import "../online/online.css"
import 'suneditor/dist/css/suneditor.min.css';

const brand = []
const productType = []
const tags = []
const SKUCodeTypeOriginal = [
     { value: 'autoGenerated', label: 'Auto-Generated'},
     { value: 'custom', label: 'Custom'},
     { value: 'ean', label: 'EAN'},
     { value: 'isbn', label: 'ISBN'},
     { value: 'itf', label: 'ITF'},
     { value: 'jan', label: 'JAN'},
     { value: 'upc', label: 'UPC'}
]
const SKUCodeTypeAdd = [
     { value: 'custom', label: 'Custom'},
     { value: 'ean', label: 'EAN'},
     { value: 'isbn', label: 'ISBN'},
     { value: 'itf', label: 'ITF'},
     { value: 'jan', label: 'JAN'},
     { value: 'upc', label: 'UPC'}
]
const TaxArray = [
     { value: 'default', label: 'Default Tax for this outlet'},
     { value: 'noTax', label: 'No Tax(0%)'},
]

const AddProduct = () => {
     const { addToast } = useToasts();
     const [open, setOpen] = useState(false);
     const [typeModalOpen, setTypeModalOpen] = useState(false);
     const [newBrandName, setNewBrandName] = useState('');
     const [newProductTypeName, setNewProductTypeName] = useState('');
     const [newBrandDescription, setNewBrandDescription] = useState('');
     const [addCreateSelect, setAddCreateSelect] = useState({});
     const [productCardActive, setProductCardActive] = useState(1);
     const [selectSKUCode, setSelectSKUCode] = useState(1);
     const [isShowNoData, setShowNoData] = useState(true);
     const [isShowNoData_1, setShowNoData_1] = useState(true);
     var   [increaseAddCode, setIncreaseAddCode] = useState(0);
     var   [increaseSupplier, setIncreaseSupplier] = useState(0);
     var   [increaseRelationShip, setIncreaseRelationShip] = useState(0);
     const [newSKUType, setNewSKUType] = useState([]);
     const [newSupplier, setNewSupplier] = useState([]);
     const [newRelationShip, setNewRelationShip] = useState([]);
     const [newRelationShip_1, setNewRelationShip_1] = useState([]);
     const [scrollPosition, setScrollPosition] = useState(0);
     const getUploadParams = ({ meta }) => { return { url: 'https://httpbin.org/post' } }
     const handleChangeStatus = ({ meta, file }, status) => { console.log(status, meta, file) }
     var AppendBody = [];
     var AppendSupplierBody = [];
     var AppendRelationShip = [];
     var AppendRelationShip_1 = [];

     const handleClose = () => {
          setOpen(false);
     };

     const typeModalClose = () => {
          setTypeModalOpen(false)
     }
     
     const handleChange = (e) => {
          if(e && e.__isNew__) {
               setAddCreateSelect({ value: e.label, label: e.value })
               setNewBrandName(e.value)
               setOpen(true)
          }
          
     };

     const prodcutTypeChange = (e) => {
          if(e && e.__isNew__) {
               setAddCreateSelect({ value: e.label, label: e.value })
               setNewProductTypeName(e.value)
               setTypeModalOpen(true)
          }
     }
     
     const addBrand = () => {
          var data = { name: newBrandName, description: newBrandDescription }
          axios.post("brand/addBrand", data)
               .then(res => {
                    if(res.status === 200) {
                         addToast(res.data.msg, { appearance: 'success' });
                         brand.push(addCreateSelect)
                         setOpen(false)
                         return
                    }
                    addToast(res.data.msg, { appearance: 'error' });
               })
     }

     const addProductType = () => {
          var data = { type: newProductTypeName }
          axios.post("productType/addProductType", data)
               .then(res => {
                    if(res.status === 200) {
                         addToast(res.data.msg, { appearance: 'success' });
                         productType.push(addCreateSelect)
                         setTypeModalOpen(false)
                         return
                    }
                    addToast(res.data.msg, { appearance: 'error' });
               })
     }

     const addBrandDescription = (value) => {
          setNewBrandDescription(value)
     }

     const addChangeBrandName = (val) => {
          console.log(addCreateSelect)
          setAddCreateSelect({value: addCreateSelect.value, label: val})
          setNewBrandName(val)
     }

     const addChangeProductTypeName = (val) => {
          setNewProductTypeName(val)
     }
     
     const handleSubmit = (files, allFiles) => {
          console.log(files.map(f => f.meta))
          allFiles.forEach(f => f.remove())
     }

     const chooseProduct = (selected) => {
          setProductCardActive(selected)
     }

     const removeSKUCode = () => {
          setIncreaseAddCode(--increaseAddCode)
          append(increaseAddCode)
     }

     const addSKUCode = () => {
          setIncreaseAddCode(++increaseAddCode)
          append(increaseAddCode)
     }

     const append = (index) => {
          AppendBody = []
          for (var i = 0; i < index; i += 1) {
               AppendBody.push(<div className='row' key={i}>
                              <div className='col-md-5'>
                                   <p>SKU Code Type</p>
                                   <Select options={SKUCodeTypeAdd} />
                              </div>
                              <div className='col-md-5'>
                                   <p>SKU Code</p>
                                   <input className='vend-settings-input fullWidth max-height-input' />
                              </div>
                              <div className='col-md-2 remove'>
                                   <span onClick={() => removeSKUCode()}><IoTrashOutline /></span>
                              </div>
                         </div>);
          }
          setNewSKUType(AppendBody)
     }

     const addNewSupplier = () => {
          setIncreaseSupplier(++increaseSupplier)
          appendNewSupplier(increaseSupplier)
     }

     const removeNewSupplier = () => {
          setIncreaseSupplier(--increaseSupplier)
          appendNewSupplier(increaseSupplier)
     }

     const appendNewSupplier = (index) => {
          AppendSupplierBody = []
          for (var i = 0; i < index; i += 1) {
               AppendSupplierBody.push(<div className='row' key={i}>
                                             <div className='col-md-4'>
                                                  <CreatableSelect
                                                       isClearable
                                                       onChange={(e) => handleChange(e)}
                                                       options={brand}
                                                       placeholder="Enter a tag name" /> 
                                             </div>
                                             <div className='col-md-4'>
                                                  <input className='vend-settings-input fullWidth max-height-input' placeholder='Enter supplier code' />
                                             </div>
                                             <div className='col-md-3'>  
                                                  <div className='withIconInput'>
                                                       <TbCurrencyDollar />
                                                       <input className='vend-settings-input fullWidth max-height-input' placeholder='0.00' />
                                                  </div>
                                             </div>
                                             <div className='col-md-1 remove'>
                                                  <span onClick={() => removeNewSupplier()}><IoTrashOutline /></span>
                                             </div>
                                        </div>)
          }
          setNewSupplier(AppendSupplierBody)
     }

     const changeSelectSKUCode = (index) => {
          setSelectSKUCode(index)
     }

     const addProductRelatioShip = () => {
          setShowNoData(false)
          setIncreaseRelationShip(++increaseRelationShip)
          appendRelationShip(increaseRelationShip)
     }

     const removeProductRelatioShip = () => {
          setIncreaseRelationShip(--increaseRelationShip)
          appendRelationShip(increaseRelationShip)
          if(increaseRelationShip == 0) {
               setShowNoData(true)
          }
     }

     const appendRelationShip = (index) => {
          AppendRelationShip = []
          for (var i = 0; i < index; i += 1) {
               AppendRelationShip.push(<>
                                        <div className='flex' key={i}>
                                             <div className='col-md-6'>
                                                  <div className='flex'>
                                                       <div className='relative fullWidth'>
                                                            <BsSearch className='search-icon' />
                                                            <input className='vend-settings-input fullWidth search-style' placeholder='Search or scan to add a product' />
                                                       </div>
                                                       <div>
                                                            <FiArrowRight className='ComeFromRightIcon' />
                                                       </div>
                                                  </div>                                   
                                             </div>
                                             <div className='col-md-6'>
                                                  <div className='between'>
                                                       <div className='flex'>
                                                            <div className='third-body'>
                                                                 <input className='vend-settings-input fullWidth max-height-input' placeholder='Qty' />
                                                            </div>
                                                            <div className='relationShip'>
                                                                 <span className='margin-zero'>Product Name</span>
                                                                 <span className='description margin-zero'>10035</span>
                                                            </div>
                                                       </div>
                                                       <div>
                                                            <div className='remove'>
                                                                 <span onClick={() => removeProductRelatioShip()}><IoTrashOutline /></span>
                                                            </div>
                                                       </div>
                                                  </div>
                                             </div>
                                        </div>
                                        <div className='divider'></div>

               </>);
          }
          setNewRelationShip(AppendRelationShip)
     }

     const addProductRelatioShip_1 = () => {
          setShowNoData_1(false)
          AppendRelationShip_1 = []
          // for (var i = 0; i < index; i += 1) {
               AppendRelationShip_1.push(<>
                                        <div className='flex'>
                                             <div className='col-md-6'>
                                                  <div className='flex fullWidth'>
                                                       <div className='relationShip'>
                                                            <span className='margin-zero'>Product Name</span>
                                                            <span className='description margin-zero'>10035</span>
                                                       </div>
                                                       <div>
                                                            <FiArrowRight className='ComeFromRightIcon' />
                                                       </div>
                                                  </div>                                   
                                             </div>
                                             <div className='col-md-6'>
                                                  <div className='between'>
                                                       <div className='third-body'>
                                                            <input className='vend-settings-input fullWidth max-height-input' placeholder='Qty' />
                                                       </div>
                                                       <div className='relative fullWidth'>
                                                            <BsSearch className='search-icon' />
                                                            <input className='vend-settings-input fullWidth search-style' placeholder='Search or scan to add a product' />
                                                       </div>
                                                       <div>
                                                            <div className='remove'>
                                                                 <span onClick={() => removeProductRelatioShip_1()}><IoTrashOutline /></span>
                                                            </div>
                                                       </div>
                                                  </div>
                                             </div>
                                        </div>
                                        <div className='divider'></div>

               </>);
          // }
          setNewRelationShip_1(AppendRelationShip_1)

     }

     const removeProductRelatioShip_1 = () => {
          setShowNoData_1(true)
          AppendRelationShip_1 = []
          setNewRelationShip_1(AppendRelationShip_1)
     }

     useEffect(() => {
          window.addEventListener("scroll", handleScroll);
          axios.post("/brand")
               .then(res => {
                    res.data.data.map((item, index) => {
                         var obj = {}
                         obj.value = item.id
                         obj.label = item.name
                         brand.push(obj)
                    })
               })
          axios.post("/productType")
               .then(res => {
                    res.data.data.map((item, index) => {
                         var obj = {}
                         obj.value = item.id
                         obj.label = item.type
                         productType.push(obj)
                    })
               })
     }, []);

     const handleScroll = () => {
          const position = window.pageYOffset;
          setScrollPosition(position)
     };

     const tagsChange = (e) => {
          tags.push({value: e.value, label: e.label})
     }
     
     return(
          <>
               <div className='online-header'>
                    <h2>
                         <Link to='/catalog/products'><FiArrowLeft className='navigate-before-icon' /></Link> 
                         New Product
                    </h2>
               </div>
               <div className={scrollPosition >= 100 ? 'online-title between fixed-header':'online-title between'}>
                    <div>
                         <p>Add, view and edit your products in one place. <Link to='' className=''>Need help?</Link></p>
                    </div>
                    <div className='products-header'>
                         <Button className='vd-btn--supplementary'>Cancel</Button>
                         <Button >Save</Button>
                    </div>
               </div>

               <div className='componentPadding flex'>
                    <div className='col-md-3'>
                         <h4>General</h4>
                         <p>Change general information for this product.</p>
                    </div>
                    <div className='col-md-9 marginTop'>
                         <div className='flex input-header-margin'>
                              <div className='half-body'>
                                   <p>Name</p>
                                   <input className='vend-settings-input fullWidth max-height-input' />
                              </div>
                              <div className='margin'></div>
                              <div className='half-body'>
                                   <p>Brand</p>
                                   <CreatableSelect
                                        isClearable
                                        onChange={(e) => handleChange(e)}
                                        options={brand}
                                        placeholder="Choose a brand" />
                              </div>
                         </div>

                         <div className="marginTop"></div>

                         <div className='input-header-margin'>
                              <p>Description</p>
                              <SunEditor />
                         </div>

                         <div className="marginTop"></div>

                         <div className='input-header-margin'>
                              <p><strong>Tags</strong> Describe the product using relevant keywords for easy filtering.</p>
                              <CreatableSelect
                                   isMulti
                                   onChange={(e) => tagsChange(e)}
                                   options={tags}
                                   placeholder="Enter a tag name" /> 
                         </div>

                         <div className="marginTop"></div>

                         <div className='input-header-margin'>
                              <p><strong>Product Type</strong> Categorize your products with types that can be used to filter sales and inventory reports.</p>
                              <CreatableSelect
                                   isClearable
                                   onChange={(e) => prodcutTypeChange(e)}
                                   options={productType}
                                   placeholder="Enter a tag name" /> 
                         </div>

                         <div className='imageSize'>
                              <FormControlLabel
                                   value="end"
                                   control={<Checkbox />}
                                   label="Sell on Point-of-Sale"
                                   labelPlacement="end"/>
                              <p className='description'>Make this product active and available for sale in-store</p>
                         </div>

                         <div className='marginTop'></div>

                         <div>
                              <Dropzone
                                   getUploadParams={getUploadParams}
                                   onChangeStatus={handleChangeStatus}
                                   onSubmit={handleSubmit}
                                   accept="image/*"
                              />
                         </div>
                    </div>
               </div>

               <div className='divider'></div>

               <div className='componentPadding flex'>

                    <div className='col-md-3'>
                         <h4>Inventory</h4>
                         <p>The type of product we choose determines how we manage inventory and reporting.</p>
                    </div>
                    <div className='col-md-9'>
                         <div className='flex text-align-center'>
                              <div className={productCardActive == 1 ? 'col-md-4 choose-product-cards choose-product choose-product-card-active' : 'col-md-4 choose-product-cards choose-product'} onClick={() => chooseProduct(1)}>
                                   <h4>Standard Product</h4>
                                   <p>This product is a single SKU with its own inventory.</p>
                              </div>    
                              <div className={productCardActive == 2 ? 'col-md-4 choose-product-cards choose-product choose-product-card-active' : 'col-md-4 choose-product-cards choose-product'} onClick={() => chooseProduct(2)}>
                                   <h4>Variant Product</h4>
                                   <p>This is a group of similar products which have different attributes, like size or color. Each variant is a unique SKU with its own inventory.</p>
                              </div>
                              <div className={productCardActive == 3 ? 'col-md-4 choose-product-cards choose-product choose-product-card-active' : 'col-md-4 choose-product-cards choose-product'} onClick={() => chooseProduct(3)}>
                                   <h4>Composite Product</h4>
                                   <p>This product is made up of specified quantities of one or more products. A composite is a single SKU but uses the inventory of the products within it.</p>
                              </div>
                         </div>

                         {productCardActive == 1 ?
                              <div>
                                   <h5>SKU CODES</h5>
                                   <div className='flex input-header-margin'>
                                        <div className='half-body'>
                                             <div className='row'>
                                                  <div className='col-md-5'>
                                                       <p>SKU Code Type</p>
                                                       <Select options={SKUCodeTypeOriginal} />
                                                  </div>
                                                  <div className='col-md-7'>
                                                       <p>SKU Code</p>
                                                       <input className='vend-settings-input fullWidth max-height-input' />
                                                  </div>
                                             </div>
                                             {newSKUType}

                                             <Button onClick={() => addSKUCode()}><AiOutlinePlus />Add Another Code</Button>
                                        </div>

                                        <div className='margin'></div>

                                        <div className='half-body'>
                                             <p>Preview</p>
                                             <div className='SKU-preview'>
                                                  <div className='flex'>
                                                       <div className="vd-id-badge__image" ></div>
                                                       <div>
                                                            <p>Product Name</p>
                                                            <p className='description'>generated SKU code</p>
                                                       </div>
                                                  </div>    
                                                  <div className='divider'></div>
                                                  <p className='description'>The first SKU code will be shown to staff and customers to help identify this product. When you have multiple codes all the barcodes will be scannable.</p>
                                             </div>
                                        </div>
                                   </div>

                                   <div className='divider'></div>

                              </div> : ''
                         }  

                         {productCardActive == 1 || productCardActive == 2 ? 
                              <>
                                   <h5>SUPPLIER INFORMATION</h5>
                                   <div className='row input-header-margin'>
                                        <div className='col-md-4'><p>Supplier</p></div>
                                        <div className='col-md-4'><p>Supplier Code</p></div>
                                        <div className='col-md-4'><p>Supplier Price</p></div>
                                   </div>
                                   <div className='divider'></div>
                                        <div className='row'>
                                             <div className='col-md-4'>
                                                  <CreatableSelect
                                                       isClearable
                                                       onChange={(e) => handleChange(e)}
                                                       options={brand}
                                                       placeholder="Enter a tag name" />
                                             </div>
                                             <div className='col-md-4'>
                                                  <input className='vend-settings-input fullWidth max-height-input' placeholder='Enter supplier code' />
                                             </div>
                                             <div className='col-md-4'>
                                                  <div className='withIconInput'>
                                                       <TbCurrencyDollar />
                                                       <input className='vend-settings-input fullWidth max-height-input' placeholder='Enter the amount' />
                                                  </div>
                                             </div>
                                        </div>

                                   {newSupplier}

                                   <Button onClick={() => addNewSupplier()}><AiOutlinePlus />Add Another Supplier</Button>
                                   
                                   <div className='divider'></div>

                                   <h5>INVENTORY LEVELS</h5>
                                   <div className='imageSize'>
                                        <FormControlLabel
                                             value="end"
                                             control={<Checkbox />}
                                             label="Track Inventory for this product"
                                             labelPlacement="end"/>
                                        <p className='description'>Manage orders, transfers and returns accurately, and get reports and insights on this product’s performance.</p>
                                   </div>
                              </> : ''
                         }      

                         {productCardActive == 3 ?
                              <>
                                   <RadioGroup
                                        aria-labelledby="demo-radio-buttons-group-label"
                                        defaultValue="auto"
                                        name="radio-buttons-group">
                                        <div className='imageSize'>
                                             <FormControlLabel value="auto" control={<Radio onChange={() => changeSelectSKUCode(1)} />} label="Automatically Generate a SKU" />
                                             {selectSKUCode == 1 ? 
                                                  <p className='description'>A SKU will automatically be generated once this product has been saved</p>
                                                  : ''
                                             }
                                        </div>
                                        <div>
                                             <FormControlLabel value="custom" control={<Radio onChange={() => changeSelectSKUCode(2)} />} label="Enter a custom SKU" />
                                             {selectSKUCode == 2 ?
                                                  <div className='paddingLeft'>
                                                       <input className='vend-settings-input fullWidth max-height-input' />
                                                       <p className='description'>Only use lower case letters and numbers, no symbols or spaces</p>
                                                  </div> : ''
                                             }
                                        </div>
                                   </RadioGroup>
                              </> : ''
                         }       

                         {productCardActive == 1 ? 
                              <>
                                   <div className='row input-header-margin'>
                                        <div className='col-md-3'><p>Outlet</p></div>
                                        <div className='col-md-3'><p>Current Inventory</p></div>
                                        <div className='col-md-3'><p>Re-order Point</p></div>
                                        <div className='col-md-3'><p>Re-order Quantity</p></div>
                                   </div>
                                   <div className='divider'></div>
                                   <div className='row'>
                                        <div className='col-md-3'>
                                             <p>Main Outlet</p>
                                        </div>
                                        <div className='col-md-3'>
                                             <input className='vend-settings-input fullWidth max-height-input' />
                                        </div>
                                        <div className='col-md-3'>
                                             <input className='vend-settings-input fullWidth max-height-input' />
                                        </div>
                                        <div className='col-md-3'>
                                             <input className='vend-settings-input fullWidth max-height-input' />
                                        </div>
                                   </div>
                              </> : ''
                         }          

                    </div>
               </div>

               <div className='divider'></div>

               <div className='componentPadding flex'>
                    <div className='col-md-3'>
                         <h4>Packaging</h4>
                         <p>Set up packaging relationships by linking this product to other SKUs. This helps you easily manage the inventory levels across products that come in different packaging.</p>
                    </div>
                    <div className='col-md-9'>
                         <h4>The products that this product comes from.</h4>
                         <div className='divider'></div>
                         {isShowNoData && 
                              <>
                                   <div className='no-table'>
                                        <img src="//vendfrontendassets.freetls.fastly.net/images/products/packaging-comes-from.svg" />
                                        <p>If this product comes from other products, add the products here.</p>
                                   </div>
                                   <div className='divider'></div>
                              </>
                         }
                         
                         {newRelationShip}
                         <Button onClick={() => addProductRelatioShip()}><AiOutlinePlus />Add a Product RelationShip</Button>

                         <div className='margin'></div>

                         <h4>The product that this product breaks into. </h4>
                         <span>Products can only break into one other product.</span>
                         <div className='divider'></div>

                         {isShowNoData_1 && 
                              <>
                                   <div className='no-table'>
                                        <img src="//vendfrontendassets.freetls.fastly.net/images/products/packaging-breaks-into.svg" />
                                        <p>If this product breaks into another product, add the product here.</p>
                                   </div>
                                   <div className='divider'></div>
                              </>
                         }
                         
                         {newRelationShip_1}

                         <Button onClick={() => addProductRelatioShip_1()}><AiOutlinePlus />Add a Product RelationShip</Button>

                    </div>
               </div>

               <div className='divider'></div>

               <div className='componentPadding flex'>
                    <div className='col-md-3'>
                         <h4>Tax</h4>
                    </div>
                    <div className='col-md-9'>
                         <div className='row input-header-margin'>
                              <div className='col-md-3'><p>Outlet</p></div>
                              <div className='col-md-9'><p>Tax</p></div>
                         </div>
                         <div className='divider'></div>
                         <div className='row'>
                              <div className='col-md-3'><p>Main Outlet</p></div>
                              <div className='col-md-9'>
                                   <Select options={TaxArray} />
                              </div>
                         </div>
                    </div>
               </div>

               <div className='divider'></div>

               <div className='componentPadding flex'>
                    <div className='col-md-3'>
                         <h4>Price</h4>
                    </div>
                    <div className='col-md-9'>
                         <div className='row input-header-margin'>
                              <div className='col-md-3'><p>Price Point</p></div>
                              <div className='col-md-2'><p>Outlet</p></div>
                              <div className='col-md-1'><p>Supply Price</p></div>
                              <div className='col-md-3'><p>Markup</p></div>
                              <div className='col-md-3'><p>Retail Price</p><p className='description'>Excluding Tax</p></div>
                         </div>
                         <div className='divider'></div>
                         <div className='row'>
                              <div className='col-md-3'><p>General Price Book (All Products)</p></div>
                              <div className='col-md-2'><p>All Outlets	</p></div>
                              <div className='col-md-1'><p>$0.00</p></div>
                              <div className='col-md-3'>
                                   <div className='withIconInput'>
                                        <AiOutlinePercentage />
                                        <input className='vend-settings-input fullWidth max-height-input' placeholder='Enter the amount' />
                                   </div>
                              </div>
                              <div className='col-md-3'>
                                   <div className='withIconInput'>
                                        <TbCurrencyDollar />
                                        <input className='vend-settings-input fullWidth max-height-input' placeholder='Enter the amount' />
                                   </div>
                              </div>
                         </div>
                    </div>
               </div>

               <Dialog open={open} onClose={handleClose} fullWidth >
                    <DialogTitle>Add Brand</DialogTitle>
                    <DialogContent className='input-header-margin' >
                         <p>Brand Name</p>
                         <input className='vend-settings-input fullWidth max-height-input' value={newBrandName} onChange={(e) => addChangeBrandName(e.target.value)} />
                         <div className='marginTop'></div>
                         <p>Description</p>
                         <textarea className='fullWidth vend-settings-input text-area-height' onChange={(e) => addBrandDescription(e.target.value)} placeholder='Enter a description for this brand that your staff will find useful.'></textarea>
                    </DialogContent>
                    <DialogActions>
                         <Button onClick={handleClose}>Cancel</Button>
                         <Button onClick={() => addBrand()}>Add Brand</Button>
                    </DialogActions>
               </Dialog>

               <Dialog open={typeModalOpen} onClose={typeModalClose} fullWidth >
                    <DialogTitle>Add Product Type</DialogTitle>
                    <DialogContent className='input-header-margin' >
                         <p>Product Type Name</p>
                         <input className='vend-settings-input fullWidth max-height-input' value={newProductTypeName} onChange={(e) => addChangeProductTypeName(e.target.value)} />
                    </DialogContent>
                    <DialogActions>
                         <Button onClick={typeModalClose}>Cancel</Button>
                         <Button onClick={() => addProductType()}>Add Product Type</Button>
                    </DialogActions>
               </Dialog>
          </>
     )
}
export default AddProduct;
