import React, { useState, useEffect } from 'react'
import { Link } from "react-router-dom";
import { Button } from "reactstrap";
import { FiArrowLeft } from "react-icons/fi";
import Creatable, { useCreatable } from 'react-select/creatable';
import { ActionMeta, OnChangeValue } from 'react-select';
import "../online/online.css"

const options = [
     { value: 'chocolate', label: 'Chocolate' },
     { value: 'strawberry', label: 'Strawberry' },
     { value: 'vanilla', label: 'Vanilla' }
   ]

const AddProduct = () => {

     const handleChange = (
          // newValue: OnChangeValue<ColourOption, false>,
          // actionMeta: ActionMeta<ColourOption>
     ) => {
          console.group('Value Changed');
          console.log(newValue);
          console.log(`action: ${actionMeta.action}`);
          console.groupEnd();
     };
     const handleInputChange = (inputValue, actionMeta) => {
          console.group('Input Changed');
          console.log(inputValue);
          console.log(`action: ${actionMeta.action}`);
          console.groupEnd();
     };

     return(
          <>
               <div className='online-header'>
                    <h2>
                         <Link to='/catalog/products'><FiArrowLeft className='navigate-before-icon' /></Link> 
                         New Product
                    </h2>
               </div>
               <div className='online-title between'>
                    <div>
                         <p>Add, view and edit your products in one place. <Link to='' className=''>Need help?</Link></p>
                    </div>
                    <div className='products-header'>
                         <Button className='vd-btn--supplementary'>Cancel</Button>
                         <Button >Save</Button>
                    </div>
               </div>

               <div className='componentPadding flex'>
                    <div className='col-md-3'>
                         <h4>General</h4>
                         <p>Change general information for this product.</p>
                    </div>
                    <div className='col-md-9 marginTop'>
                         <div className='flex input-header-margin'>
                              <div className='half-body'>
                                   <p>Name</p>
                                   <input className='vend-settings-input fullWidth max-height-input' />
                              </div>
                              <div className='half-body'>
                                   <p>Brand</p>
                                   <CreatableSelect
                                        isClearable
                                        onChange={() => handleChange()}
                                        onInputChange={() => handleInputChange()}
                                        options={options}
                                   />
                              </div>
                         </div>
                    </div>
               </div>
          </>
     )
}
export default AddProduct;
