import React, { useState, useEffect } from 'react'
import { Link } from "react-router-dom";
import { Button } from "reactstrap";
import { FiArrowLeft } from "react-icons/fi";
import CreatableSelect from 'react-select/creatable';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import SunEditor from 'suneditor-react';
import Checkbox from '@mui/material/Checkbox';
import FormControlLabel from '@mui/material/FormControlLabel';
import "../online/online.css"
import 'suneditor/dist/css/suneditor.min.css';

const options = [
     { value: 'chocolate', label: 'Chocolate' },
     { value: 'strawberry', label: 'Strawberry' },
     { value: 'vanilla', label: 'Vanilla' }
   ]

const AddProduct = () => {
     const [open, setOpen] = useState(false);
     const [newBrandName, setNewBrandName] = useState('');
     const handleClose = () => {
          setOpen(false);
     };
     const handleChange = (e) => {
          if(e && e.__isNew__) {
               options.push({ value: e.label, label: e.value })
               setNewBrandName(e.value)
               setOpen(true)
          }
          
     };
     const addBrand = () => {
          console.log("123123")
     }

     return(
          <>
               <div className='online-header'>
                    <h2>
                         <Link to='/catalog/products'><FiArrowLeft className='navigate-before-icon' /></Link> 
                         New Product
                    </h2>
               </div>
               <div className='online-title between'>
                    <div>
                         <p>Add, view and edit your products in one place. <Link to='' className=''>Need help?</Link></p>
                    </div>
                    <div className='products-header'>
                         <Button className='vd-btn--supplementary'>Cancel</Button>
                         <Button >Save</Button>
                    </div>
               </div>

               <div className='componentPadding flex'>
                    <div className='col-md-3'>
                         <h4>General</h4>
                         <p>Change general information for this product.</p>
                    </div>
                    <div className='col-md-9 marginTop'>
                         <div className='flex input-header-margin'>
                              <div className='half-body'>
                                   <p>Name</p>
                                   <input className='vend-settings-input fullWidth max-height-input' />
                              </div>
                              <div className='margin'></div>
                              <div className='half-body'>
                                   <p>Brand</p>
                                   <CreatableSelect
                                        isClearable
                                        onChange={(e) => handleChange(e)}
                                        options={options}
                                        placeholder="Choose a brand" />
                              </div>
                         </div>

                         <div className="marginTop"></div>

                         <div className='input-header-margin'>
                              <p>General</p>
                              <SunEditor />
                         </div>

                         <div className="marginTop"></div>

                         <div className='input-header-margin'>
                              <p><strong>Tags</strong> Describe the product using relevant keywords for easy filtering.</p>
                              <CreatableSelect
                                   isMulti
                                   onChange={(e) => handleChange(e)}
                                   options={options}
                                   placeholder="Enter a tag name" /> 
                         </div>

                         <div className="marginTop"></div>

                         <div className='input-header-margin'>
                              <p><strong>Product Type</strong> Categorize your products with types that can be used to filter sales and inventory reports.</p>
                              <CreatableSelect
                                   isClearable
                                   onChange={(e) => handleChange(e)}
                                   options={options}
                                   placeholder="Enter a tag name" /> 
                         </div>

                         <div className='imageSize'>
                              <FormControlLabel
                                   value="end"
                                   control={<Checkbox />}
                                   label="Sell on Point-of-Sale"
                                   labelPlacement="end"/>
                              <p className='description'>Make this product active and available for sale in-store</p>
                         </div>
                    </div>
               </div>

               <Dialog open={open} onClose={handleClose} fullWidth >
                    <DialogTitle>Add Brand</DialogTitle>
                    <DialogContent className='input-header-margin' >
                         <p>Brand Name</p>
                         <input className='vend-settings-input fullWidth max-height-input' value={newBrandName} />
                         <div className='marginTop'></div>
                         <p>Description</p>
                         <textarea className='fullWidth vend-settings-input text-area-height' placeholder='Enter a description for this brand that your staff will find useful.'></textarea>
                    </DialogContent>
                    <DialogActions>
                         <Button onClick={handleClose}>Cancel</Button>
                         <Button onClick={() => addBrand()}>Add Brand</Button>
                    </DialogActions>
               </Dialog>
          </>
     )
}
export default AddProduct;
