import React, { useState } from 'react'
import { Link } from "react-router-dom";
import { Row, Col, Button } from "reactstrap";
import PropTypes from 'prop-types';
import { useTheme } from '@mui/material/styles';
import { Box, Tabs, Tab, InputAdornment, InputLabel, Select, MenuItem, OutlinedInput } from '@mui/material';
import AccountCircle from '@mui/icons-material/AccountCircle';
import "./sell.css"

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

const AllSales = [
     {
          key: "All",
          allKey: true,
          value: "All Sales"
     },
     {
          key: "AllOpenSales",
          allKey: false,
          continueSale: true,
          value: "All Open Sales"
     },
     {
          key: "Layby",
          allKey: true,
          continueSale: true,
          value: "Layby"
     },
     {
          key: "OnAccount",
          allKey: true,
          continueSale: true,
          value: "On Account"
     },
     {
          key: "Parked",
          allKey: true,
          continueSale: true,
          value: "Parked"
     },
     {
          key: "DeliveryUnfulfilled",
          allKey: true,
          value: "Delivery Unfulfilled"
     },
     {
          key: "PickupUnfulfilled",
          allKey: true,
          value: "Pickup Unfulfilled"
     },
     {
          key: "AllClosedSales",
          allKey: false,
          processReturn: true,
          value: "All Closed Sales"
     },
     {
          key: "Completed",
          allKey: true,
          processReturn: true,
          value: "Completed"
     },
     {
          key: "LaybeCompleted",
          allKey: true,
          processReturn: true,
          value: "Laybe Completed"
     },
     {
          key: "OnAccountCompleted",
          allKey: true,
          processReturn: true,
          value: "On Account Completed"
     },
     {
          key: "DeliveryCompleted",
          allKey: true,
          processReturn: true,
          value: "Delivery Completed"
     },
     {
          key: "PickupCompleted",
          allKey: true,
          processReturn: true,
          value: "Pickup Completed"
     },
     {
          key: "Voided",
          allKey: true,
          value: "Voided"
     },
];

TabPanel.propTypes = {
     children: PropTypes.node,
     index: PropTypes.number.isRequired,
     value: PropTypes.number.isRequired,
};

function TabPanel(props) {
     const { children, value, index, ...other } = props;
   
     return (
          <div
               role="tabpanel"
               hidden={value !== index}
               id={`simple-tabpanel-${index}`}
               aria-labelledby={`simple-tab-${index}`}
               {...other}
           >
          {value === index && (
                <div>{children}</div>
          )}
       </div>
     );
}

function a11yProps(index) {
     return {
          id: `simple-tab-${index}`,
          'aria-controls': `simple-tabpanel-${index}`,
     };
}

function getStyles(name, personName, theme) {
  return {
    fontWeight:
      personName.indexOf(name) === -1
        ? theme.typography.fontWeightRegular
        : theme.typography.fontWeightMedium,
  };
}

const SaleHistory = () => {
     const theme = useTheme();
     const [value, setValue] = useState(0);
     const [isShow, setShowHide] = useState(false);
     const [personName, setPersonName] = useState([]);

     const handleChange = (event, newValue) => {
          setValue(newValue);
     };

     const selectChange = (event) => {
          const {
               target: { value },
          } = event;
          setPersonName(
               typeof value === 'string' ? value.split(',') : value,
          );
     };

     const moreFilter = () => {
          setShowHide(!isShow)
     }

     return(
          <div className='OpenClose'>
               <div>
                    <h2>Sales History</h2>
               </div>
                    
               <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                    <Tabs value={value} onChange={handleChange} TabIndicatorProps={{style: {background:'#3f32f5', color: 'green'}}}>
                         <Tab label="All" {...a11yProps(0)} />
                         <Tab label="Process return" {...a11yProps(1)} />
                         <Tab label="Continue sale" {...a11yProps(2)} />
                    </Tabs>
               </Box>
               <TabPanel value={value} index={0}>
                    <div className='OpenCloseHeader'>
                         <div><p>View, edit and manage your sales all in one place. <Link to=''>Need help?</Link></p></div>
                    </div> 
               </TabPanel>
               <TabPanel value={value} index={1}>
                    <div className='OpenCloseHeader'>
                         <div><p>Retrieve a sale to process a return as an exchange or refund. <Link to=''>Need help?</Link></p></div>
                    </div>
               </TabPanel>
               <TabPanel value={value} index={2}>
                    <div className='OpenCloseHeader'>
                         <div><p>Continue processing open sales. <Link to=''>Need help?</Link></p></div>
                    </div>
               </TabPanel>

                <div className='SalesHistory'>
                    <Row>
                         <Col xxl='3' md='3' className='tabControl'>
                              <InputLabel htmlFor="SaleHistoryStatus">
                                   Status
                              </InputLabel>
                              <Select
                                   labelId="SaleHistoryStatus"
                                   id="demo-multiple-name"
                                   fullWidth
                                   value={personName}
                                   onChange={selectChange}
                                   input={<OutlinedInput label="Name" />}
                                   MenuProps={MenuProps}
                              >
                                   {AllSales.map((name, index) => (
                                        <MenuItem
                                             key={name.key}
                                             value={name.value}
                                             style={getStyles(name.value, personName, theme)} >

                                             {name.allkey && name.value}
                                        </MenuItem>
                                   ))}
                              </Select>
                         </Col>
                         <Col xxl='3' md='3' className='tabControl'> 
                              <InputLabel htmlFor="customer">
                              Customer
                              </InputLabel>
                              <OutlinedInput
                                   id="customer"
                                   fullWidth
                                   placeholder='Search for customers'
                                   startAdornment={
                                        <InputAdornment position="start">
                                        <AccountCircle />
                                        </InputAdornment>
                                   }
                              />
                         </Col>
                         <Col xxl='3' md='3' className='tabControl'>
                              <InputLabel htmlFor="receiptOrNote">
                                   Receipt or Note
                              </InputLabel>
                              <OutlinedInput
                                   id="receiptOrNote"
                                   fullWidth
                                   placeholder='Search receipt number or note'
                              />
                         </Col>
                         <Col xxl='3' md='3' className={isShow ? 'tabControl show' : 'tabControl hidden'}>
                         
                         </Col>
                         <Col xxl='3' md='3' className={isShow ? 'tabControl show' : 'tabControl hidden'}>
                              <InputLabel htmlFor="outlet">
                                   Outlet
                              </InputLabel>
                              {/* <Select
                                   labelId="outlet"
                                   id="demo-multiple-name"
                                   fullWidth
                                   value={personName}
                                   onChange={selectChange}
                                   input={<OutlinedInput label="Name" />}
                                   MenuProps={MenuProps}
                              >
                                   {names.map((name) => (
                                   <MenuItem
                                   key={name}
                                   value={name}
                                   style={getStyles(name, personName, theme)}
                                   >
                                   {name}
                                   </MenuItem>
                                   ))}
                              </Select> */}
                         </Col>
                         <Col xxl='3' md='3' className={isShow ? 'tabControl show' : 'tabControl hidden'}>
                              <InputLabel htmlFor="user">
                                   User
                              </InputLabel>
                              {/* <Select
                                   labelId="user"
                                   id="demo-multiple-name"
                                   fullWidth
                                   value={personName}
                                   onChange={selectChange}
                                   input={<OutlinedInput label="Name" />}
                                   MenuProps={MenuProps}
                              >
                                   {names.map((name) => (
                                   <MenuItem
                                   key={name}
                                   value={name}
                                   style={getStyles(name, personName, theme)}
                                   >
                                   {name}
                                   </MenuItem>
                                   ))}
                              </Select> */}
                         </Col>
                         <Col xxl='3' md='3' className='SaleHistory-moreInfo'>
                              <div>
                                   <Link to='' onClick={ () => moreFilter() }>More filters</Link>
                                   <Button variant="contained">Search</Button>
                              </div>
                         </Col>
                    </Row>
               </div>  

          </div>
     )
}
export default SaleHistory;
