//     icon: "bi bi-textarea-resize",
//   },
//   {
//     title: "Breadcrumbs",
//     href: "/breadcrumbs",
//     icon: "bi bi-link",
//   },
//   {
//     title: "About",
//     href: "/about",
//     icon: "bi bi-people",
//   },
// ];

const navigation = [
  {
    id: "tab_0",
    title: "Home",
    icon: "bi bi-house-fill",
  },
  {
    id: "tab_1",
    title: "Alert",
    icon: "bi bi-bell",
  },
  {
    id: "tab_2",
    title: "Badges",
    icon: "bi bi-patch-check",
  },
  {
    id: "tab_3",
    title: "Buttons",
    icon: "bi bi-hdd-stack",
  },
  {
    id: "tab_4",
    title: "Cards",
    icon: "bi bi-card-text",
  },
];

const Sidebar = () => {
  const changeBackground = (id) => {
    this.props.getTabId
    var elems = document.querySelectorAll(".nav-link.text-secondary.py-3.active");
    [].forEach.call(elems, function(el) {
        el.classList.remove("active");
    });
    document.getElementById(id).className += " active"
  }

  return (
    <div>
      <div>
        <Nav vertical className="sidebarNav">
          {navigation.map((navi, index) => (
            <NavItem className="sidenav-bg sideNavTab" onClick={() => changeBackground(navi.id)}>
              <Link id={navi.id} to='#' className="nav-link text-secondary py-3 active">
                <i className={navi.icon}></i>
                <span className="d-inline-block">{navi.title}</span>
              </Link>
            </NavItem>
          ))}
        </Nav>
      </div>

    </div>

  );
};

export default Sidebar;
