  //support epoch
  if (typeof tmp === 'number') {
    return tmp
  }
  //suport date objects
  if (isDate(tmp)) {
    return tmp.getTime()
  }
  if (tmp.epoch) {
    return tmp.epoch
  }
  return null
}

