  const sign = offset > 0 ? '+' : '-'
  const absOffset = Math.abs(offset)
  const hours = zeroPad(parseInt('' + absOffset, 10))
  const minutes = zeroPad((absOffset % 1) * 60)
  return `${sign}${hours}${delimiter}${minutes}`
