    s = getNow(s)
    s = s.hour(18) //6pm
    return s
  },
  tomorrow: (s) => {
    s = getNow(s)
    s = s.add(1, 'day')
    s = s.startOf('day')
    return s
  },
  yesterday: (s) => {
    s = getNow(s)
    s = s.subtract(1, 'day')
    s = s.startOf('day')
