const units = {
  year: {
    valid: (n) => n > -4000 && n < 4000,
    walkTo: (s, n) => walk(s, n, 'getFullYear', 'year', null)
  },
  month: {
    valid: (n) => n >= 0 && n <= 11,
    walkTo: (s, n) => {
      let d = s.d
      let current = d.getMonth()
      let original = s.epoch
      let startUnit = d.getFullYear()
      if (current === n) {
        return
      }
      //try to get it as close as we can..
      let diff = n - current
      s.epoch += ms.day * (diff * 28) //special case
      //oops, did we change the year? revert it.
      if (startUnit !== s.d.getFullYear()) {
        s.epoch = original
      }
      //increment by day
      while (s.d.getMonth() < n) {
        s.epoch += ms.day
      }
      while (s.d.getMonth() > n) {
        s.epoch -= ms.day
      }
    }
  },
  date: {
    valid: (n) => n > 0 && n <= 31,
    walkTo: (s, n) => walk(s, n, 'getDate', 'day', 'getMonth')
  },
  hour: {
    valid: (n) => n >= 0 && n < 24,
    walkTo: (s, n) => walk(s, n, 'getHours', 'hour', 'getDate')
  },
  minute: {
    valid: (n) => n >= 0 && n < 60,
    walkTo: (s, n) => walk(s, n, 'getMinutes', 'minute', 'getHours')
  },
  second: {
    valid: (n) => n >= 0 && n < 60,
    walkTo: (s, n) => {
      //do this one directly
      s.epoch = s.seconds(n).epoch
    }
  },
  millisecond: {
    valid: (n) => n >= 0 && n < 1000,
    walkTo: (s, n) => {
      //do this one directly
      s.epoch = s.milliseconds(n).epoch
    }
  }
}

const walkTo = (s, wants) => {
  let keys = Object.keys(units)
  let old = s.clone()
  for (let i = 0; i < keys.length; i++) {
    let k = keys[i]
    let n = wants[k]
    if (n === undefined) {
      n = old[k]()
    }
    if (typeof n === 'string') {
      n = parseInt(n, 10)
    }
    //make-sure it's valid
    if (!units[k].valid(n)) {
      s.epoch = null
      if (s.silent === false) {
        console.warn('invalid ' + k + ': ' + n)
      }
      return
    }
    units[k].walkTo(s, n)
  }
  return
}

