  if (prog[unit] !== undefined) {
    // go forward one?
    if (prog[unit] > 0.5) {
      s = s.add(1, unit)
    }
    // go to start
    s = s.startOf(unit)
  } else if (s.silent === false) {
    console.warn("no known unit '" + unit + "'")
  }
  return s
}
