const startOf = (a, unit) => {
  let s = a.clone()
  unit = normalize(unit)
  if (units[unit]) {
    return units[unit](s)
  }
  if (unit === 'summer' || unit === 'winter') {
    s = s.season(unit)
    return units.season(s)
  }
  return s
}

//piggy-backs off startOf
const endOf = (a, unit) => {
  let s = a.clone()
  unit = normalize(unit)
  if (units[unit]) {
    // go to beginning, go to next one, step back 1ms
    s = units[unit](s) // startof
    s = s.add(1, unit)
    s = s.subtract(1, 'millisecond')
    return s
  }
  return s
}
