    if (diff < 1) {
      diff += 1
    }
    shift = diff * ms.hour
    s.epoch -= shift
  }
  walkTo(s, {
    hour: n
  })
  confirm(s, old, 'minute')
  s = fwdBkwd(s, old, goFwd, 'day') // specify direction
  return s.epoch
}

const time = function (s, str, goFwd) {
  let m = str.match(/([0-9]{1,2})[:h]([0-9]{1,2})(:[0-9]{1,2})? ?(am|pm)?/)
