    if (n > 30) {
      //change this in 10y
      n = 1900 + n
    } else {
      // '12 is 2012
      n = 2000 + n
    }
  }
  n = validate(n)
  walkTo(s, {
    year: n
  })
  return s.epoch
}

const week = function (s, n, goFwd) {
  let old = s.clone()
  n = validate(n)
  s = s.month(0)
  s = s.date(1)
  s = s.day('monday')
