    s = s.add(1, 'week')
  }
  n -= 1 //1-based
  s = s.add(n, 'weeks')
  s = fwdBkwd(s, old, goFwd, 'year') // specify direction
  return s.epoch
}

const dayOfYear = function (s, n, goFwd) {
  n = validate(n)
  let old = s.clone()
  n -= 1 //days are 1-based
  if (n <= 0) {
    n = 0
  } else if (n >= 365) {
    n = 364
  }
  s = s.startOf('year')
  s = s.add(n, 'day')
  confirm(s, old, 'hour')
  s = fwdBkwd(s, old, goFwd, 'year') // specify direction
  return s.epoch
}

