const addMethods = (SpaceTime) => {
  SpaceTime.prototype.isSame = function (b, unit, tzAware = true) {
    let a = this
    if (!unit) {
      return null
    }
    // support swapped params
    if (typeof b === 'string' && typeof unit === 'object') {
      let tmp = b
      b = unit
      unit = tmp
    }
