import React, { useState } from 'react'
import { styled, alpha } from '@mui/material/styles';
import Button from '@mui/material/Button';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import { ReactComponent as Wallet } from "../../assets/images/logos/wallet.svg";
import IOSSwitch from '../IOSSwitch'

const StyledMenu = styled((props) => (
  <Menu
    elevation={0}
    anchorOrigin={{
      vertical: 'bottom',
      horizontal: 'right',
    }}
    transformOrigin={{
      vertical: 'top',
      horizontal: 'right',
    }}
    {...props}
  />
))(({ theme }) => ({
  '& .MuiPaper-root': {
    borderRadius: 6,
    marginTop: theme.spacing(1),
    minWidth: 180,
    color:
      theme.palette.mode === 'light' ? 'rgb(55, 65, 81)' : theme.palette.grey[300],
    boxShadow:
      'rgb(255, 255, 255) 0px 0px 0px 0px, rgba(0, 0, 0, 0.05) 0px 0px 0px 1px, rgba(0, 0, 0, 0.1) 0px 10px 15px -3px, rgba(0, 0, 0, 0.05) 0px 4px 6px -2px',
    '& .MuiMenu-list': {
      padding: '4px 0',
    },
    '& .MuiMenuItem-root': {
      '& .MuiSvgIcon-root': {
        fontSize: 18,
        color: theme.palette.text.secondary,
        marginRight: theme.spacing(1.5),
      },
      '&:active': {
        backgroundColor: alpha(
          theme.palette.primary.main,
          theme.palette.action.selectedOpacity,
        ),
      },
    },
  },
}));

const Payments = () => {

     const [anchorEl, setAnchorEl] = React.useState(null);
     const open = Boolean(anchorEl);
     const handleClick = (event) => {
          setAnchorEl(event.currentTarget);
     };
     const handleClose = () => {
          setAnchorEl(null);
     };
     
     return(
          <div className='componentPadding'>
               <h4>Current payment methods</h4>
               <p>These are the payment methods that you have set up in your store. If enabled, they are available for your customers to choose from at checkout.</p>

               <div className='online-settings-body '>
                    <div className='payment-method-body'>
                         <div className='flex payment-method-wallet'>
                              <span>
                                   <Wallet />
                              </span>
                              <div >
                                   <p className='name margin_0'>Pay by cash</p>  
                                   <p className='description margin_0'>Manual payment: Settings</p>  
                              </div>
                         </div>

                         <div>
                              <Button
                                   id="demo-customized-button"
                                   aria-controls={open ? 'demo-customized-menu' : undefined}
                                   aria-haspopup="true"
                                   aria-expanded={open ? 'true' : undefined}
                                   variant="contained"
                                   disableElevation
                                   onClick={handleClick}
                                   endIcon={<KeyboardArrowDownIcon />} >
                                        Actions
                              </Button>

                              <StyledMenu
                                   id="demo-customized-menu"
                                   MenuListProps={{
                                        'aria-labelledby': 'demo-customized-button',
                                   }}
                                   anchorEl={anchorEl}
                                   open={open}
                                   onClose={handleClose} >
                                   <MenuItem onClick={handleClose} disableRipple>
                                        Edit
                                   </MenuItem>
                                   <MenuItem onClick={handleClose} disableRipple>
                                        Remove
                                   </MenuItem>
                              </StyledMenu>
                              <IOSSwitch />
                         </div>
                    </div>
               </div>
          </div>
     )
}

export default Payments;
