const navigation = [
  {
    title: "Home",
    icon: "bi bi-house-fill",
  },
  {
    title: "Alert",
    icon: "bi bi-bell",
  },
  {
    title: "Badges",
    icon: "bi bi-patch-check",
  },
  {
    title: "Buttons",
    icon: "bi bi-hdd-stack",
  },
  {
