import { useState, useEffect } from "react";
import { Outlet, useLocation } from "react-router-dom";
import { Container } from "reactstrap";
import Sidebar from "./Sidebar";
import Header from "./Header";
import SidebarList from "./SidebarList";

const FullLayout = () => {

  let location = useLocation();
  const [tabId, setTabId] = useState('');

  const Paths = [
    {
      tabId: 'tab_1',
      path: 'sell'
    },
    {
      tabId: 'tab_2',
      path: 'sell_1'
    },
    {
      tabId: 'tab_3',
      path: 'sell_2'
    }
  ]

  const showMobilemenu = () => {
    document.getElementById("sidebarArea").classList.toggle("showSidebar");
  };

  const getTabId = (id) => {
    setTabId(id)
  }
  
  useEffect(() => {
    const pathname = location.pathname.split('/')[1]
    const goPage = Paths.find((v) => v.path === pathname)
    console.log(pathname)
    console.log(goPage.tabId)
    setTabId(goPage.tabId)
  }, [])
    

  return (
    <main>
      {/********header**********/}
      <Header />
      <div className="pageWrapper d-lg-flex" style={{paddingTop: '60px'}}>
        {/********Sidebar**********/}
        <aside className="sidebarArea shadow" id="sidebarArea">
          <Sidebar getTabId = {getTabId} />
          <SidebarList tabId = {tabId} />
          <div className=" sidebarToggle" onClick={() => showMobilemenu()} >
            <i className="bi bi-x toggleColor"></i>
          </div>
        </aside>
        {/********Content Area**********/}
        <div className="contentArea">
          {/********Middle Content**********/}
          <Container className="p-4" fluid>
            <Outlet />
          </Container>
        </div>
      </div>
    </main>
  );
};

export default FullLayout;
